	  		// update symbol dimension by its scale
			var newTransformSymbol = 'scale(' + newScaleValue + ', ' + newScaleValue + ')';
			// Cas points
			svgSymbolPoint0_0B = document.getElementById('SymbolBDynLayer00Main');
			svgSymbolPoint0_0S = document.getElementById('SymbolSDynLayer00Main');
			svgSymbolPoint1_0B = document.getElementById('SymbolBDynLayer10Main');
			svgSymbolPoint1_0S = document.getElementById('SymbolSDynLayer10Main');

			svgSymbolTarget = document.getElementById('targetObjectMain');
  
			if (svgSymbolPoint0_0B) {
				// On fait ces controles car c'est defini dans le <defs> qui n'est pas encore forcement charge
				// lors de la lecture initiale du fichier svg (cause asynchrone!!!)
		 		svgSymbolPoint0_0B.getElementsByTagName("g").item(0).setAttributeNS(null,'transform', newTransformSymbol);
		 		svgSymbolPoint0_0S.getElementsByTagName("g").item(0).setAttributeNS(null,'transform', newTransformSymbol);

		 		svgSymbolPoint1_0B.getElementsByTagName("g").item(0).setAttributeNS(null,'transform', newTransformSymbol);
