		// stepValue : la valeur d'incrementation entre deux objets dans la liste (20 pour les layers et legende, 15 pour attrib)
		stepValue = 20;
		// totalHeight --> pour attrib:nbAttrib*stepValue; pour layer:layerControlHeight; pour legend:currentLegendHeight
 		totalHeight = layerControlHeight;
		//MyAlert("totalHeight="+totalHeight);
		minY = scrollbarHeight - totalHeight - 20; // 10 correspond a une marge esthetique
		//MyAlert("minY="+minY);
