			// initialHeightPerc (pourcentage doit etre compris entre 0 et 1) = scrollbarHeight/totalHeight
			initialHeightPerc = scrollbarHeight/totalHeight;
			// scrollStep (pourcentage de deplacement unitaire doit etre compris entre 0 et 1) = stepValue/scrollbarHeight
