			}
			//create text elements
			var caption = document.createElementNS(svgNS,"text");
			caption.setAttributeNS(null,"id","TabInfo"+indexNr);
			caption.setAttributeNS(null,"x","200");
			caption.setAttributeNS(null,"y","150");
			caption.setAttributeNS(null,"font-family",fontFamily);
			caption.setAttributeNS(null,"fill",colorStroke);
