svg.script:
var second = 1;
function init(evt)
{
	svgdoc = evt.target.ownerDocument;
	
	setInterval("getData(second)", 1000);
}
function getData(sec)
{
	getURL("http://localhost/amfphp/services/org/zgflex/cookbook/services/Circle.php",displayCallback);
	second++;
}

function displayCallback(data)
{
	if(data.success)
	{	
		string = data.content;
		node = parseXML(string, svgdoc);
		svgdoc.rootElement.appendChild(node);
	}
}
